-- Safe migration for production\nBEGIN;\n-- Ensure no null states before setting NOT NULL\nUPDATE "public"."Rotation" SET "state"='NOT_STARTED' WHERE "state" IS NULL;\nALTER TABLE "public"."Rotation" ALTER COLUMN "state" SET NOT NULL;\n\n-- Convert Verification.status enum to TEXT without data loss\nALTER TABLE "public"."Verification" ALTER COLUMN "status" TYPE TEXT USING "status"::text;\nALTER TABLE "public"."Verification" ALTER COLUMN "status" SET DEFAULT 'PENDING';\nALTER TABLE "public"."Verification" ALTER COLUMN "status" SET NOT NULL;\n-- Drop enum type if it exists (ignore error if not)\nDO 71340 BEGIN IF EXISTS (SELECT 1 FROM pg_type WHERE typname = 'VerificationStatus') THEN EXECUTE 'DROP TYPE "public"."VerificationStatus"'; END IF; END 71340;\n\n-- New tables (additive)\nCREATE TABLE IF NOT EXISTS "public"."QuizResult" (\n    "id" TEXT NOT NULL,\n    "internId" TEXT NOT NULL,\n    "itemId" TEXT NOT NULL,\n    "itemType" TEXT NOT NULL,\n    "passed" BOOLEAN NOT NULL,\n    "score" INTEGER,\n    "createdAt" TIMESTAMP(3) NOT NULL DEFAULT CURRENT_TIMESTAMP,\n    "updatedAt" TIMESTAMP(3) NOT NULL,\n    CONSTRAINT "QuizResult_pkey" PRIMARY KEY ("id")\n);\n\nCREATE TABLE IF NOT EXISTS "public"."Reflection" (\n    "id" TEXT NOT NULL,\n    "internId" TEXT NOT NULL,\n    "itemId" TEXT NOT NULL,\n    "itemType" TEXT NOT NULL,\n    "content" TEXT NOT NULL,\n    "image1Url" TEXT,\n    "image2Url" TEXT,\n    "createdAt" TIMESTAMP(3) NOT NULL DEFAULT CURRENT_TIMESTAMP,\n    "updatedAt" TIMESTAMP(3) NOT NULL,\n    CONSTRAINT "Reflection_pkey" PRIMARY KEY ("id")\n);\n\nCREATE TABLE IF NOT EXISTS "public"."Case" (\n    "id" TEXT NOT NULL,\n    "title" TEXT NOT NULL,\n    "category" TEXT NOT NULL,\n    "description" TEXT NOT NULL,\n    "image1Url" TEXT,\n    "image2Url" TEXT,\n    "image3Url" TEXT,\n    "createdAt" TIMESTAMP(3) NOT NULL DEFAULT CURRENT_TIMESTAMP,\n    "updatedAt" TIMESTAMP(3) NOT NULL,\n    "authorId" TEXT NOT NULL,\n    CONSTRAINT "Case_pkey" PRIMARY KEY ("id")\n);\n\nCREATE TABLE IF NOT EXISTS "public"."Comment" (\n    "id" TEXT NOT NULL,\n    "content" TEXT NOT NULL,\n    "caseId" TEXT NOT NULL,\n    "authorId" TEXT NOT NULL,\n    "parentId" TEXT,\n    "createdAt" TIMESTAMP(3) NOT NULL DEFAULT CURRENT_TIMESTAMP,\n    "updatedAt" TIMESTAMP(3) NOT NULL,\n    CONSTRAINT "Comment_pkey" PRIMARY KEY ("id")\n);\n\nCREATE TABLE IF NOT EXISTS "public"."Favorite" (\n    "id" TEXT NOT NULL,\n    "userId" TEXT NOT NULL,\n    "caseId" TEXT NOT NULL,\n    "createdAt" TIMESTAMP(3) NOT NULL DEFAULT CURRENT_TIMESTAMP,\n    CONSTRAINT "Favorite_pkey" PRIMARY KEY ("id")\n);\n\nCREATE TABLE IF NOT EXISTS "public"."ProcedureKnowledgeFavorite" (\n    "id" TEXT NOT NULL,\n    "userId" TEXT NOT NULL,\n    "itemId" TEXT NOT NULL,\n    "itemType" TEXT NOT NULL,\n    "createdAt" TIMESTAMP(3) NOT NULL DEFAULT CURRENT_TIMESTAMP,\n    CONSTRAINT "ProcedureKnowledgeFavorite_pkey" PRIMARY KEY ("id")\n);\n\nCREATE TABLE IF NOT EXISTS "public"."MentorFeedback" (\n    "id" TEXT NOT NULL,\n    "internId" TEXT NOT NULL,\n    "mentorId" TEXT NOT NULL,\n    "itemId" TEXT NOT NULL,\n    "itemType" TEXT NOT NULL,\n    "content" TEXT NOT NULL,\n    "isRead" BOOLEAN NOT NULL DEFAULT false,\n    "createdAt" TIMESTAMP(3) NOT NULL DEFAULT CURRENT_TIMESTAMP,\n    "updatedAt" TIMESTAMP(3) NOT NULL,\n    CONSTRAINT "MentorFeedback_pkey" PRIMARY KEY ("id")\n);\n\nCREATE TABLE IF NOT EXISTS "public"."TutorFavoriteIntern" (\n    "id" TEXT NOT NULL,\n    "tutorId" TEXT NOT NULL,\n    "internId" TEXT NOT NULL,\n    "createdAt" TIMESTAMP(3) NOT NULL DEFAULT CURRENT_TIMESTAMP,\n    CONSTRAINT "TutorFavoriteIntern_pkey" PRIMARY KEY ("id")\n);\n\n-- Indexes\nCREATE INDEX IF NOT EXISTS "QuizResult_internId_idx" ON "public"."QuizResult"("internId");\nCREATE INDEX IF NOT EXISTS "QuizResult_itemId_itemType_idx" ON "public"."QuizResult"("itemId", "itemType");\nCREATE UNIQUE INDEX IF NOT EXISTS "QuizResult_internId_itemId_itemType_key" ON "public"."QuizResult"("internId", "itemId", "itemType");\nCREATE INDEX IF NOT EXISTS "Reflection_internId_idx" ON "public"."Reflection"("internId");\nCREATE INDEX IF NOT EXISTS "Reflection_itemId_itemType_idx" ON "public"."Reflection"("itemId", "itemType");\nCREATE UNIQUE INDEX IF NOT EXISTS "Reflection_internId_itemId_itemType_key" ON "public"."Reflection"("internId", "itemId", "itemType");\nCREATE INDEX IF NOT EXISTS "Case_category_idx" ON "public"."Case"("category");\nCREATE INDEX IF NOT EXISTS "Case_createdAt_idx" ON "public"."Case"("createdAt");\nCREATE INDEX IF NOT EXISTS "Comment_caseId_idx" ON "public"."Comment"("caseId");\nCREATE INDEX IF NOT EXISTS "Comment_parentId_idx" ON "public"."Comment"("parentId");\nCREATE INDEX IF NOT EXISTS "Favorite_userId_idx" ON "public"."Favorite"("userId");\nCREATE INDEX IF NOT EXISTS "Favorite_caseId_idx" ON "public"."Favorite"("caseId");\nCREATE UNIQUE INDEX IF NOT EXISTS "Favorite_userId_caseId_key" ON "public"."Favorite"("userId", "caseId");\nCREATE INDEX IF NOT EXISTS "ProcedureKnowledgeFavorite_userId_idx" ON "public"."ProcedureKnowledgeFavorite"("userId");\nCREATE INDEX IF NOT EXISTS "ProcedureKnowledgeFavorite_itemId_itemType_idx" ON "public"."ProcedureKnowledgeFavorite"("itemId", "itemType");\nCREATE UNIQUE INDEX IF NOT EXISTS "ProcedureKnowledgeFavorite_userId_itemId_itemType_key" ON "public"."ProcedureKnowledgeFavorite"("userId", "itemId", "itemType");\nCREATE INDEX IF NOT EXISTS "MentorFeedback_internId_idx" ON "public"."MentorFeedback"("internId");\nCREATE INDEX IF NOT EXISTS "MentorFeedback_mentorId_idx" ON "public"."MentorFeedback"("mentorId");\nCREATE INDEX IF NOT EXISTS "MentorFeedback_itemId_itemType_idx" ON "public"."MentorFeedback"("itemId", "itemType");\nCREATE INDEX IF NOT EXISTS "MentorFeedback_internId_itemId_itemType_idx" ON "public"."MentorFeedback"("internId", "itemId", "itemType");\nCREATE INDEX IF NOT EXISTS "TutorFavoriteIntern_tutorId_idx" ON "public"."TutorFavoriteIntern"("tutorId");\nCREATE INDEX IF NOT EXISTS "TutorFavoriteIntern_internId_idx" ON "public"."TutorFavoriteIntern"("internId");\nCREATE UNIQUE INDEX IF NOT EXISTS "TutorFavoriteIntern_tutorId_internId_key" ON "public"."TutorFavoriteIntern"("tutorId", "internId");\nCREATE INDEX IF NOT EXISTS "Verification_status_idx" ON "public"."Verification"("status");\n\n-- Foreign keys\nALTER TABLE "public"."QuizResult" ADD CONSTRAINT "QuizResult_internId_fkey" FOREIGN KEY ("internId") REFERENCES "public"."User"("id") ON DELETE CASCADE ON UPDATE CASCADE;\nALTER TABLE "public"."Reflection" ADD CONSTRAINT "Reflection_internId_fkey" FOREIGN KEY ("internId") REFERENCES "public"."User"("id") ON DELETE CASCADE ON UPDATE CASCADE;\nALTER TABLE "public"."Case" ADD CONSTRAINT "Case_authorId_fkey" FOREIGN KEY ("authorId") REFERENCES "public"."User"("id") ON DELETE CASCADE ON UPDATE CASCADE;\nALTER TABLE "public"."Comment" ADD CONSTRAINT "Comment_caseId_fkey" FOREIGN KEY ("caseId") REFERENCES "public"."Case"("id") ON DELETE CASCADE ON UPDATE CASCADE;\nALTER TABLE "public"."Comment" ADD CONSTRAINT "Comment_authorId_fkey" FOREIGN KEY ("authorId") REFERENCES "public"."User"("id") ON DELETE CASCADE ON UPDATE CASCADE;\nALTER TABLE "public"."Comment" ADD CONSTRAINT "Comment_parentId_fkey" FOREIGN KEY ("parentId") REFERENCES "public"."Comment"("id") ON DELETE SET NULL ON UPDATE CASCADE;\nALTER TABLE "public"."Favorite" ADD CONSTRAINT "Favorite_userId_fkey" FOREIGN KEY ("userId") REFERENCES "public"."User"("id") ON DELETE CASCADE ON UPDATE CASCADE;\nALTER TABLE "public"."Favorite" ADD CONSTRAINT "Favorite_caseId_fkey" FOREIGN KEY ("caseId") REFERENCES "public"."Case"("id") ON DELETE CASCADE ON UPDATE CASCADE;\nALTER TABLE "public"."ProcedureKnowledgeFavorite" ADD CONSTRAINT "ProcedureKnowledgeFavorite_userId_fkey" FOREIGN KEY ("userId") REFERENCES "public"."User"("id") ON DELETE CASCADE ON UPDATE CASCADE;\nALTER TABLE "public"."MentorFeedback" ADD CONSTRAINT "MentorFeedback_internId_fkey" FOREIGN KEY ("internId") REFERENCES "public"."User"("id") ON DELETE CASCADE ON UPDATE CASCADE;\nALTER TABLE "public"."MentorFeedback" ADD CONSTRAINT "MentorFeedback_mentorId_fkey" FOREIGN KEY ("mentorId") REFERENCES "public"."User"("id") ON DELETE CASCADE ON UPDATE CASCADE;\nALTER TABLE "public"."TutorFavoriteIntern" ADD CONSTRAINT "TutorFavoriteIntern_tutorId_fkey" FOREIGN KEY ("tutorId") REFERENCES "public"."User"("id") ON DELETE CASCADE ON UPDATE CASCADE;\nALTER TABLE "public"."TutorFavoriteIntern" ADD CONSTRAINT "TutorFavoriteIntern_internId_fkey" FOREIGN KEY ("internId") REFERENCES "public"."User"("id") ON DELETE CASCADE ON UPDATE CASCADE;\n\nCOMMIT;\n